package Assignment25112023;

public class Assignment31 {
	final static double pi = 3.14;

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double r = 10;
		double circ = 2 * pi * r;
		System.out.println("circumference of circle = " + circ);
	}
}
